const crypto=require("crypto"),fs=require("fs");module.exports={encrypt:async function(e,r){const t=await fs.readFileSync(r,"utf8"),c=Buffer.from(t,"base64"),o=crypto.randomBytes(16);let f=crypto.createCipheriv("aes-256-cbc",Buffer.from(c),o),a=f.update(e);return{cipher:(a=Buffer.concat([a,f.final()])).toString("base64"),iv:o.toString("base64")}}};