const{contextBridge:contextBridge,remote:remote}=require("electron");const{dialog:dialog}=require("electron").remote;const keygenAES=require("./js/backend/lib/aes_keygen_lib");const win=remote.getCurrentWindow();contextBridge.exposeInMainWorld("winCtl",{close:()=>{win.close()},max:()=>{if(win.isFullScreen())win.setFullScreen(false);else win.setFullScreen(true)},min:()=>{win.minimize()},restore:()=>{win.unmaximize()}});contextBridge.exposeInMainWorld("fileOps",{fileOpen:(customTitle,dialogOps)=>{return dialog.showOpenDialog(win,{title:customTitle,message:customTitle,properties:dialogOps})},filePick:(customTitle,label,ops,filter)=>{return dialog.showSaveDialog(win,{title:customTitle,message:customTitle,nameFieldLabel:label,properties:ops,filters:filter})}});contextBridge.exposeInMainWorld("cryptoFunc",{AESKeygen:filename=>{return keygenAES.gen(filename)}});require("electron");