const{contextBridge:contextBridge,remote:remote,dialog:dialog}=require("electron");const win=remote.getCurrentWindow();contextBridge.exposeInMainWorld("winCtl",{close:()=>{win.close()},max:()=>{if(win.isFullScreen())win.setFullScreen(false);else win.setFullScreen(true)},min:()=>{win.minimize()},restore:()=>{win.unmaximize()},filePicker:any=>{return dialog.showOpenDialog({properties:["openFile"]})}});